ble(connection); // 3. Insert sample data insertEmployee(connection, 1, \\"John Doe\\", \\"Software Engineer\\", 90000); insertEmployee(connection, 2, \\"Jane Smith\\", \\"Project Manager\\", 95000); // 4. Read all employees readEmployees(connection); // 5. Update an employee's salary updateEmployeeSalary(connection, 1, 92000); // 6. Delete an employee by ID deleteEmployee(connection, 2); // 7. Close the connection connection.close(); } catch (SQLException e) { System.err.println(\\"Error: \\" + e.getMessage()); e.printStackTrace(); } } private static void createEmployeesTable(Connection connection) throws SQLException { Statement statement = connection.createStatement(); String sql = \\"CREATE TABLE IF NOT EXISTS employees (\\" + \\"id SERIAL PRIMARY KEY,\\" + \\"name VARCHAR(255) NOT NULL,\\" + \\"position VARCHAR(255) NOT NULL,\\" + \\"salary INTEGER NOT NULL\\" + \\")\\"; statement.executeUpdate(sql); statement.close(); } private static void insertEmployee(Connection connection, int id, String name, String position, int salary) throws SQLException { String sql = \\"INSERT INTO employees (id, name, position, salary) VALUES (?, ?, ?, ?)\\"; PreparedStatement preparedStatement = connection.prepareStatement(sql); preparedStatement.setInt(1, id); preparedStatement.setString(2, name); preparedStatement.setString(3, position); preparedStatement.setInt(4, salary); preparedStatement.executeUpdate(); preparedStatement.close(); } private static void readEmployees(Connection connection) throws SQLException { Statement statement = connection.createStatement(); ResultSet resultSet = statement.executeQuery(\\"SELECT * FROM employees\\"); while (resultSet.next()) { System.out.println(\\"ID: \\" + resultSet.getInt(\\"id\\") + \\", Name: \\" + resultSet.getString(\\"name\\") + \\", Position: \\" + resultSet.getString(\\"position\\") + \\", Salary: \\" + resultSet.getInt(\\"salary\\")); } resultSet.close(); statement.close(); } private static void updateEmployeeSalary(Connection c